import { useQuery } from "@tanstack/react-query";
import { ApiPromise, WsProvider } from "@polkadot/api";
import { ethers, JsonRpcProvider, BrowserProvider } from "ethers";
import { getCachedData, setCachedData } from "@/lib/cache";
import type { WalletState } from "./useMultiWallet";
import { PARACHAINS, ETHEREUM_CONFIG, type ParachainConfig } from "@/config/parachains";

export interface ChainBalance {
  chainId: string;
  chainName: string;
  balance: string;
  balanceNumeric: number;
  usdValue: string;
  symbol: string;
  lastUpdated: string;
  status: "online" | "syncing" | "offline" | "cached";
}

async function getSubstrateBalance(
  config: ParachainConfig,
  address: string
): Promise<ChainBalance> {
  let api: ApiPromise | null = null;

  try {
    api = await ApiPromise.create({
      provider: new WsProvider(config.rpcUrl),
    });

    const accountInfo = await api.query.system.account(address);
    const accountData = accountInfo.toJSON() as any;
    const free = accountData?.data?.free || "0";

    // Use proper decimal formatting to preserve fractional precision
    const divisor = BigInt("1" + "0".repeat(config.decimals));
    const freeBalance = BigInt(free);
    const wholePart = freeBalance / divisor;
    const fractionalPart = freeBalance % divisor;

    // Format with proper decimals (e.g., "123.456789")
    const fractionalStr = fractionalPart.toString().padStart(config.decimals, "0");
    const balance = `${wholePart}.${fractionalStr}`.replace(/\.?0+$/, "") || "0";
    const balanceNumeric = parseFloat(balance);
    const usdValue = (balanceNumeric * (config.mockUsdPrice || 0)).toFixed(2);

    return {
      chainId: config.id,
      chainName: config.name,
      balance,
      balanceNumeric,
      usdValue,
      symbol: config.symbol,
      lastUpdated: new Date().toISOString(),
      status: "online",
    };
  } catch (error) {
    console.error(`${config.name} balance fetch error:`, error);
    return {
      chainId: config.id,
      chainName: config.name,
      balance: "0",
      balanceNumeric: 0,
      usdValue: "0",
      symbol: config.symbol,
      lastUpdated: new Date().toISOString(),
      status: "offline",
    };
  } finally {
    if (api) {
      try {
        await api.disconnect();
      } catch (disconnectError) {
        console.error(`Error disconnecting from ${config.name}:`, disconnectError);
      }
    }
  }
}

async function getEvmBalance(
  config: typeof ETHEREUM_CONFIG | ParachainConfig,
  address: string,
  useBrowserProvider = false
): Promise<ChainBalance> {
  try {
    const provider = useBrowserProvider && window.ethereum
      ? new BrowserProvider(window.ethereum)
      : new JsonRpcProvider(config.rpcUrl);

    const balance = await provider.getBalance(address);
    const balanceInEth = ethers.formatUnits(balance, config.decimals);
    const balanceNumeric = parseFloat(balanceInEth);
    const usdValue = (balanceNumeric * (config.mockUsdPrice || 0)).toFixed(2);

    return {
      chainId: config.id,
      chainName: config.name,
      balance: balanceInEth,
      balanceNumeric,
      usdValue,
      symbol: config.symbol,
      lastUpdated: new Date().toISOString(),
      status: "online",
    };
  } catch (error) {
    console.error(`${config.name} balance fetch error:`, error);
    return {
      chainId: config.id,
      chainName: config.name,
      balance: "0",
      balanceNumeric: 0,
      usdValue: "0",
      symbol: config.symbol,
      lastUpdated: new Date().toISOString(),
      status: "offline",
    };
  }
}

export function useMultiChainBalances(walletState: WalletState) {
  return useQuery({
    queryKey: ["/api/multichain/balances", walletState.polkadot.address, walletState.ethereum.address],
    queryFn: async () => {
      const balances: ChainBalance[] = [];

      // Fetch all parachain balances if Polkadot connected
      if (walletState.polkadot.connected && walletState.polkadot.address) {
        const address = walletState.polkadot.address;

        // Map parachains to balance fetch promises (only Substrate chains for Polkadot wallet)
        const parachainFetches = PARACHAINS.filter(config => config.type === "substrate")
          .map(async (config) => {
            const cacheKey = `balance:${config.id}:${address}`;
            const cached = getCachedData<ChainBalance>(cacheKey);

            try {
              const balance = await getSubstrateBalance(config, address);
              setCachedData(cacheKey, balance);
              return balance;
            } catch (error) {
              console.debug(`${config.name} balance fetch failed, using cache:`, error);
              if (cached) {
                // Ensure cached data has balanceNumeric (for backwards compatibility)
                const cachedBalance = cached.data;
                if (cachedBalance.balanceNumeric === undefined) {
                  cachedBalance.balanceNumeric = parseFloat(cachedBalance.balance) || 0;
                }
                return { ...cachedBalance, status: "cached" as const };
              }
              // Return offline placeholder
              return {
                chainId: config.id,
                chainName: config.name,
                balance: "0",
                balanceNumeric: 0,
                usdValue: "0",
                symbol: config.symbol,
                lastUpdated: new Date().toISOString(),
                status: "offline" as const,
              };
            }
          });

        // Use Promise.allSettled to handle failures gracefully
        const results = await Promise.allSettled(parachainFetches);
        results.forEach((result) => {
          if (result.status === "fulfilled") {
            balances.push(result.value);
          }
        });
      }

      // Fetch Ethereum and EVM parachain balances if MetaMask connected
      if (walletState.ethereum.connected && walletState.ethereum.address) {
        const address = walletState.ethereum.address;

        // Fetch Ethereum mainnet balance
        const ethCacheKey = `balance:${ETHEREUM_CONFIG.id}:${address}`;
        const ethCached = getCachedData<ChainBalance>(ethCacheKey);

        try {
          const ethBalance = await getEvmBalance(ETHEREUM_CONFIG, address, true);
          setCachedData(ethCacheKey, ethBalance);
          balances.push(ethBalance);
        } catch (error) {
          console.debug("Ethereum balance fetch failed, using cache:", error);
          if (ethCached) {
            // Ensure cached data has balanceNumeric (for backwards compatibility)
            const cachedBalance = ethCached.data;
            if (cachedBalance.balanceNumeric === undefined) {
              cachedBalance.balanceNumeric = parseFloat(cachedBalance.balance) || 0;
            }
            balances.push({ ...cachedBalance, status: "cached" as const });
          } else {
            balances.push({
              chainId: ETHEREUM_CONFIG.id,
              chainName: ETHEREUM_CONFIG.name,
              balance: "0",
              balanceNumeric: 0,
              usdValue: "0",
              symbol: ETHEREUM_CONFIG.symbol,
              lastUpdated: new Date().toISOString(),
              status: "offline",
            });
          }
        }

        // Fetch EVM-compatible parachain balances (e.g., Moonbeam)
        const evmParachainFetches = PARACHAINS.filter(config => config.type === "evm")
          .map(async (config) => {
            const cacheKey = `balance:${config.id}:${address}`;
            const cached = getCachedData<ChainBalance>(cacheKey);

            try {
              const balance = await getEvmBalance(config, address, false);
              setCachedData(cacheKey, balance);
              return balance;
            } catch (error) {
              console.debug(`${config.name} balance fetch failed, using cache:`, error);
              if (cached) {
                // Ensure cached data has balanceNumeric (for backwards compatibility)
                const cachedBalance = cached.data;
                if (cachedBalance.balanceNumeric === undefined) {
                  cachedBalance.balanceNumeric = parseFloat(cachedBalance.balance) || 0;
                }
                return { ...cachedBalance, status: "cached" as const };
              }
              return {
                chainId: config.id,
                chainName: config.name,
                balance: "0",
                balanceNumeric: 0,
                usdValue: "0",
                symbol: config.symbol,
                lastUpdated: new Date().toISOString(),
                status: "offline" as const,
              };
            }
          });

        const evmResults = await Promise.allSettled(evmParachainFetches);
        evmResults.forEach((result) => {
          if (result.status === "fulfilled") {
            balances.push(result.value);
          }
        });
      }

      return balances;
    },
    enabled: walletState.polkadot.connected || walletState.ethereum.connected,
    refetchInterval: 60000, // Refresh every minute
    staleTime: 30000,
  });
}

client/src/contexts/WalletContext.tsx.content:
import { createContext, useContext, useState, useEffect, ReactNode } from "react";
import { web3Accounts, web3Enable } from "@polkadot/extension-dapp";

export interface PolkadotAccount {
  address: string;
  meta: {
    name?: string;
    source: string;
  };
}

export interface WalletState {
  polkadot: {
    connected: boolean;
    address: string | null;
    accounts: PolkadotAccount[];
  };
  ethereum: {
    connected: boolean;
    address: string | null;
  };
}

interface WalletContextValue {
  walletState: WalletState;
  isConnecting: boolean;
  error: string | null;
  connectPolkadot: () => Promise<any>;
  connectEthereum: () => Promise<string>;
  disconnectPolkadot: () => void;
  disconnectEthereum: () => void;
  disconnectAll: () => void;
  selectPolkadotAccount: (address: string) => void;
  isPolkadotConnected: boolean;
  isEthereumConnected: boolean;
  isAnyConnected: boolean;
  activePolkadotAccount: PolkadotAccount | null;
}

const WalletContext = createContext<WalletContextValue | undefined>(undefined);

export function WalletProvider({ children }: { children: ReactNode }) {
  const [walletState, setWalletState] = useState<WalletState>({
    polkadot: {
      connected: false,
      address: null,
      accounts: [],
    },
    ethereum: {
      connected: false,
      address: null,
    },
  });
  const [isConnecting, setIsConnecting] = useState(false);
  const [error, setError] = useState<string | null>(null);
  const [hasInitialized, setHasInitialized] = useState(false);

  const connectPolkadot = async () => {
    setIsConnecting(true);
    setError(null);

    try {
      console.log("Connecting to Polkadot.js extension...");

      // Detect Edge browser and provide specific guidance
      const isEdge = typeof navigator !== 'undefined' && /Edg/.test(navigator.userAgent);

      if (isEdge) {
        console.log("Edge browser detected - providing Edge-specific guidance");
      }

      const extensions = await Promise.race([
        web3Enable("PolkaConnect"),
        new Promise<never>((_, reject) =>
          setTimeout(() => reject(new Error("Extension connection timeout")), 10000)
        ),
      ]);

      console.log(`Found ${extensions.length} Polkadot extension(s)`);

      if (extensions.length === 0) {
        let errorMsg = "Polkadot.js extension not found. Please:\n" +
          "1. Install the extension from polkadot.js.org/extension\n" +
          "2. Enable it in your browser\n" +
          "3. Refresh this page\n" +
          "4. Grant permission when prompted";

        if (isEdge) {
          errorMsg = "Polkadot.js extension not detected on Microsoft Edge.\n\n" +
            "Edge-specific steps:\n" +
            "1. Install the extension from Microsoft Edge Add-ons or polkadot.js.org/extension\n" +
            "2. Click the puzzle icon (Extensions) in the toolbar\n" +
            "3. Ensure Polkadot.js extension is enabled\n" +
            "4. Grant permission to this site when prompted\n" +
            "5. Refresh this page and try connecting again\n\n" +
            "Note: You may need to pin the extension to see it in your toolbar.";
        }

        throw new Error(errorMsg);
      }

      const accounts = await web3Accounts();
      console.log(`Found ${accounts.length} Polkadot account(s)`);

      if (accounts.length === 0) {
        throw new Error(
          "No accounts found. Please create an account in your Polkadot.js extension."
        );
      }

      // Check if we have a previously selected address
      const savedAddress = localStorage.getItem("polkaconnect_polkadot_address");
      const selectedAccount = savedAddress && accounts.find(acc => acc.address === savedAddress)
        ? savedAddress
        : accounts[0].address;

      setWalletState(prev => ({
        ...prev,
        polkadot: {
          connected: true,
          address: selectedAccount,
          accounts: accounts,
        },
      }));

      localStorage.setItem("polkaconnect_polkadot_connected", "true");
      localStorage.setItem("polkaconnect_polkadot_address", selectedAccount);

      console.log(" Connected to Polkadot wallet:", selectedAccount);

      return accounts[0];
    } catch (err) {
      const errorMessage = err instanceof Error ? err.message : "Failed to connect Polkadot wallet";
      setError(errorMessage);
      console.error("Polkadot connection error:", err);
      throw err;
    } finally {
      setIsConnecting(false);
    }
  };

  const connectEthereum = async () => {
    setIsConnecting(true);
    setError(null);

    try {
      console.log("Connecting to MetaMask...");

      if (typeof window === 'undefined' || !window.ethereum) {
        throw new Error(
          "MetaMask not found. Please:\n" +
            "1. Install MetaMask from metamask.io\n" +
            "2. Refresh this page\n" +
            "3. Click Connect MetaMask again"
        );
      }

      // Request account access using the standard MetaMask API
      const accounts = await window.ethereum.request({ 
        method: 'eth_requestAccounts' 
      }) as string[];

      console.log(`Found ${accounts.length} MetaMask account(s)`);

      if (!accounts || accounts.length === 0) {
        throw new Error("No Ethereum accounts found. Please create an account in MetaMask.");
      }

      const address = accounts[0];

      setWalletState(prev => ({
        ...prev,
        ethereum: {
          connected: true,
          address,
        },
      }));

      localStorage.setItem("polkaconnect_ethereum_connected", "true");
      localStorage.setItem("polkaconnect_ethereum_address", address);

      console.log(" Connected to MetaMask:", address);

      return address;
    } catch (err) {
      const errorMessage = err instanceof Error ? err.message : "Failed to connect to MetaMask";
      setError(errorMessage);
      console.error("MetaMask connection error:", err);
      throw err;
    } finally {
      setIsConnecting(false);
    }
  };

  const disconnectPolkadot = () => {
    setWalletState(prev => ({
      ...prev,
      polkadot: {
        connected: false,
        address: null,
        accounts: [],
      },
    }));
    localStorage.removeItem("polkaconnect_polkadot_connected");
    localStorage.removeItem("polkaconnect_polkadot_address");
    console.log("Polkadot wallet disconnected");
  };

  const disconnectEthereum = () => {
    setWalletState(prev => ({
      ...prev,
      ethereum: {
        connected: false,
        address: null,
      },
    }));
    localStorage.removeItem("polkaconnect_ethereum_connected");
    localStorage.removeItem("polkaconnect_ethereum_address");
    console.log("Ethereum wallet disconnected");
  };

  const disconnectAll = () => {
    disconnectPolkadot();
    disconnectEthereum();
    setError(null);
  };

  const selectPolkadotAccount = (address: string) => {
    const account = walletState.polkadot.accounts.find(acc => acc.address === address);
    if (account) {
      setWalletState(prev => ({
        ...prev,
        polkadot: {
          ...prev.polkadot,
          address,
        },
      }));
      localStorage.setItem("polkaconnect_polkadot_address", address);
      console.log("Switched to Polkadot account:", address);
    }
  };

  // Auto-reconnect on mount (runs only once)
  useEffect(() => {
    if (hasInitialized) return;

    // Migrate from old single-wallet format
    const oldWalletType = localStorage.getItem("polkaconnect_wallet_type");
    if (oldWalletType) {
      if (oldWalletType === "polkadot") {
        localStorage.setItem("polkaconnect_polkadot_connected", "true");
      } else if (oldWalletType === "ethereum") {
        localStorage.setItem("polkaconnect_ethereum_connected", "true");
      }
      localStorage.removeItem("polkaconnect_wallet_type");
    }

    const polkadotConnected = localStorage.getItem("polkaconnect_polkadot_connected");
    const ethereumConnected = localStorage.getItem("polkaconnect_ethereum_connected");

    if (polkadotConnected === "true") {
      connectPolkadot().catch(console.error);
    }
    if (ethereumConnected === "true") {
      connectEthereum().catch(console.error);
    }

    setHasInitialized(true);
  }, [hasInitialized]);

  const activePolkadotAccount = walletState.polkadot.accounts.find(
    acc => acc.address === walletState.polkadot.address
  ) || null;

  const value: WalletContextValue = {
    walletState,
    isConnecting,
    error,
    connectPolkadot,
    connectEthereum,
    disconnectPolkadot,
    disconnectEthereum,
    disconnectAll,
    selectPolkadotAccount,
    isPolkadotConnected: walletState.polkadot.connected,
    isEthereumConnected: walletState.ethereum.connected,
    isAnyConnected: walletState.polkadot.connected || walletState.ethereum.connected,
    activePolkadotAccount,
  };

  return <WalletContext.Provider value={value}>{children}</WalletContext.Provider>;
}

export function useWallet() {
  const context = useContext(WalletContext);
  if (context === undefined) {
    throw new Error("useWallet must be used within a WalletProvider");
  }
  return context;
}

