client/src/hooks/useXcmData.ts.content:
import { useQuery } from "@tanstack/react-query";
import { ApiPromise, WsProvider } from "@polkadot/api";
import { getCachedData, setCachedData } from "@/lib/cache";

export interface XcmChannel {
  parachainId: number;
  name: string;
  active: boolean;
}

async function getXcmChannels(): Promise<XcmChannel[]> {
  let api: ApiPromise | null = null;

  try {
    api = await ApiPromise.create({
      provider: new WsProvider("wss://rpc.polkadot.io"),
    });

    const parachains = await api.query.paras.parachains();
    const parachainsArray = parachains.toJSON() as any[];
    const parachainIds = parachainsArray.map((para: any) => parseInt(para.toString()));

    // Map known parachain IDs to names
    const parachainNames: Record<number, string> = {
      1000: "Statemint",
      2000: "Acala",
      2004: "Moonbeam",
      2006: "Astar",
      2012: "Parallel",
      2030: "Bifrost",
      2032: "Interlay",
      2034: "HydraDX",
    };

    return parachainIds.map((id: number) => ({
      parachainId: id,
      name: parachainNames[id] || `Parachain ${id}`,
      active: true,
    }));
  } catch (error) {
    console.error("XCM channels fetch error:", error);
    return [];
  } finally {
    // Always disconnect to prevent websocket leaks
    if (api) {
      try {
        await api.disconnect();
      } catch (disconnectError) {
        console.error("Error disconnecting from Polkadot API:", disconnectError);
      }
    }
  }
}

export function useXcmData() {
  return useQuery({
    queryKey: ["/api/xcm/channels"],
    queryFn: async () => {
      const cacheKey = "xcm_channels";
      const cached = getCachedData<XcmChannel[]>(cacheKey);

      try {
        const channels = await getXcmChannels();
        setCachedData(cacheKey, channels);
        return channels;
      } catch (error) {
        console.error("XCM data fetch error:", error);
        if (cached) {
          return cached.data;
        }
        return [];
      }
    },
    refetchInterval: 300000, // Refresh every 5 minutes
    staleTime: 120000,
  });
}

server/storage.ts.content:
import { 
  type User, 
  type InsertUser, 
  type ChainBalance, 
  type Proposal, 
  type NetworkNode,
  type GovernanceVote,
  type InsertGovernanceVote,
  type Comment,
  type InsertComment,
  type UserXp,
  type InsertUserXp,
  type TransactionHistory,
  type InsertTransactionHistory,
  type XcmActivity,
  type StakingAnalytics,
  type GovernanceParticipation
} from "@shared/schema";
import { randomUUID } from "crypto";

export interface IStorage {
  getUser(id: string): Promise<User | undefined>;
  getUserByUsername(username: string): Promise<User | undefined>;
  createUser(user: InsertUser): Promise<User>;

  getCachedBalances(): ChainBalance[] | null;
  setCachedBalances(balances: ChainBalance[]): void;

  getCachedProposals(): Proposal[] | null;
  setCachedProposals(proposals: Proposal[]): void;

  getCachedNetworkNodes(): NetworkNode[] | null;
  setCachedNetworkNodes(nodes: NetworkNode[]): void;

  getCachedXcmActivity(): XcmActivity[] | null;
  setCachedXcmActivity(activity: XcmActivity[]): void;

  getCachedStakingAnalytics(): StakingAnalytics | null;
  setCachedStakingAnalytics(analytics: StakingAnalytics): void;

  getCachedGovernanceParticipation(address: string): GovernanceParticipation | null;
  setCachedGovernanceParticipation(participation: GovernanceParticipation): void;

  // Governance votes
  createVote(vote: InsertGovernanceVote): Promise<GovernanceVote>;
  getVotesByReferendum(referendumId: number): Promise<GovernanceVote[]>;
  getVotesByWallet(walletAddress: string): Promise<GovernanceVote[]>;
  getUserVoteForReferendum(referendumId: number, walletAddress: string): Promise<GovernanceVote | undefined>;

  // Comments
  createComment(comment: InsertComment): Promise<Comment>;
  getCommentsByReferendum(referendumId: number): Promise<Comment[]>;
  getCommentsByWallet(walletAddress: string): Promise<Comment[]>;

  // User XP
  getUserXp(walletAddress: string): Promise<UserXp | undefined>;
  getAllUserXp(): Promise<UserXp[]>;
  createOrUpdateUserXp(xpData: InsertUserXp): Promise<UserXp>;
  incrementVoteCount(walletAddress: string, xpGain: number): Promise<UserXp>;
  incrementCommentCount(walletAddress: string, xpGain: number): Promise<UserXp>;

  // Transaction History
  createTransaction(tx: InsertTransactionHistory): Promise<TransactionHistory>;
  getTransactionsByWallet(walletAddress: string, limit?: number): Promise<TransactionHistory[]>;
  getTransactionByHash(txHash: string): Promise<TransactionHistory | undefined>;
  updateTransactionStatus(id: string, status: "pending" | "completed" | "failed"): Promise<TransactionHistory | undefined>;

  // Community Stats
  getTotalTransactionCount(): Promise<number>;
  getUniqueWalletCount(): Promise<number>;
}

export class MemStorage implements IStorage {
  private users: Map<string, User>;
  private cachedBalances: ChainBalance[] | null = null;
  private cachedProposals: Proposal[] | null = null;
  private cachedNetworkNodes: NetworkNode[] | null = null;
  private cachedXcmActivity: XcmActivity[] | null = null;
  private cachedStakingAnalytics: StakingAnalytics | null = null;
  private cachedGovernanceParticipation: Map<string, GovernanceParticipation> = new Map();
  private governanceVotes: Map<string, GovernanceVote>;
  private comments: Map<string, Comment>;
  private userXpMap: Map<string, UserXp>;
  private transactionHistoryMap: Map<string, TransactionHistory>;

  constructor() {
    this.users = new Map();
    this.governanceVotes = new Map();
    this.comments = new Map();
    this.userXpMap = new Map();
    this.transactionHistoryMap = new Map();
  }

  async getUser(id: string): Promise<User | undefined> {
    return this.users.get(id);
  }

  async getUserByUsername(username: string): Promise<User | undefined> {
    return Array.from(this.users.values()).find(
      (user) => user.username === username,
    );
  }

  async createUser(insertUser: InsertUser): Promise<User> {
    const id = randomUUID();
    const user: User = { ...insertUser, id };
    this.users.set(id, user);
    return user;
  }

  getCachedBalances(): ChainBalance[] | null {
    return this.cachedBalances;
  }

  setCachedBalances(balances: ChainBalance[]): void {
    this.cachedBalances = balances;
  }

  getCachedProposals(): Proposal[] | null {
    return this.cachedProposals;
  }

  setCachedProposals(proposals: Proposal[]): void {
    this.cachedProposals = proposals;
  }

  getCachedNetworkNodes(): NetworkNode[] | null {
    return this.cachedNetworkNodes;
  }

  setCachedNetworkNodes(nodes: NetworkNode[]): void {
    this.cachedNetworkNodes = nodes;
  }

  getCachedXcmActivity(): XcmActivity[] | null {
    return this.cachedXcmActivity;
  }

  setCachedXcmActivity(activity: XcmActivity[]): void {
    this.cachedXcmActivity = activity;
  }

  getCachedStakingAnalytics(): StakingAnalytics | null {
    return this.cachedStakingAnalytics;
  }

  setCachedStakingAnalytics(analytics: StakingAnalytics): void {
    this.cachedStakingAnalytics = analytics;
  }

  getCachedGovernanceParticipation(address: string): GovernanceParticipation | null {
    return this.cachedGovernanceParticipation.get(address) || null;
  }

  setCachedGovernanceParticipation(participation: GovernanceParticipation): void {
    this.cachedGovernanceParticipation.set(participation.walletAddress, participation);
  }

  // Governance votes
  async createVote(insertVote: InsertGovernanceVote): Promise<GovernanceVote> {
    const id = randomUUID();
    const vote: GovernanceVote = {
      ...insertVote,
      id,
      timestamp: new Date(),
      conviction: insertVote.conviction ?? 0,
      balance: insertVote.balance ?? null,
    };
    this.governanceVotes.set(id, vote);
    return vote;
  }

  async getVotesByReferendum(referendumId: number): Promise<GovernanceVote[]> {
    return Array.from(this.governanceVotes.values()).filter(
      (vote) => vote.referendumId === referendumId
    );
  }

  async getVotesByWallet(walletAddress: string): Promise<GovernanceVote[]> {
    return Array.from(this.governanceVotes.values()).filter(
      (vote) => vote.walletAddress === walletAddress
    );
  }

  async getUserVoteForReferendum(
    referendumId: number,
    walletAddress: string
  ): Promise<GovernanceVote | undefined> {
    return Array.from(this.governanceVotes.values()).find(
      (vote) =>
        vote.referendumId === referendumId &&
        vote.walletAddress === walletAddress
    );
  }

  // Comments
  async createComment(insertComment: InsertComment): Promise<Comment> {
    const id = randomUUID();
    const comment: Comment = {
      ...insertComment,
      id,
      timestamp: new Date(),
    };
    this.comments.set(id, comment);
    return comment;
  }

  async getCommentsByReferendum(referendumId: number): Promise<Comment[]> {
    return Array.from(this.comments.values())
      .filter((comment) => comment.referendumId === referendumId)
      .sort((a, b) => b.timestamp.getTime() - a.timestamp.getTime());
  }

  async getCommentsByWallet(walletAddress: string): Promise<Comment[]> {
    return Array.from(this.comments.values()).filter(
      (comment) => comment.walletAddress === walletAddress
    );
  }

  // User XP
  async getUserXp(walletAddress: string): Promise<UserXp | undefined> {
    return this.userXpMap.get(walletAddress);
  }

  async getAllUserXp(): Promise<UserXp[]> {
    return Array.from(this.userXpMap.values())
      .sort((a, b) => b.xp - a.xp);
  }

  async createOrUpdateUserXp(xpData: InsertUserXp): Promise<UserXp> {
    const existing = this.userXpMap.get(xpData.walletAddress);
    const userXp: UserXp = {
      ...xpData,
      xp: xpData.xp ?? existing?.xp ?? 0,
      lastUpdated: new Date(),
      level: existing?.level ?? xpData.level ?? 1,
      votesCount: existing?.votesCount ?? xpData.votesCount ?? 0,
      commentsCount: existing?.commentsCount ?? xpData.commentsCount ?? 0,
    };
    this.userXpMap.set(xpData.walletAddress, userXp);
    return userXp;
  }

  async incrementVoteCount(walletAddress: string, xpGain: number): Promise<UserXp> {
    const existing = this.userXpMap.get(walletAddress) || {
      walletAddress,
      xp: 0,
      level: 1,
      votesCount: 0,
      commentsCount: 0,
      lastUpdated: new Date(),
    };

    const newXp = existing.xp + xpGain;
    const newLevel = Math.floor(newXp / 100) + 1;

    const updated: UserXp = {
      ...existing,
      xp: newXp,
      level: newLevel,
      votesCount: existing.votesCount + 1,
      lastUpdated: new Date(),
    };

    this.userXpMap.set(walletAddress, updated);
    return updated;
  }

  async incrementCommentCount(walletAddress: string, xpGain: number): Promise<UserXp> {
    const existing = this.userXpMap.get(walletAddress) || {
      walletAddress,
      xp: 0,
      level: 1,
      votesCount: 0,
      commentsCount: 0,
      lastUpdated: new Date(),
    };

    const newXp = existing.xp + xpGain;
    const newLevel = Math.floor(newXp / 100) + 1;

    const updated: UserXp = {
      ...existing,
      xp: newXp,
      level: newLevel,
      commentsCount: existing.commentsCount + 1,
      lastUpdated: new Date(),
    };

    this.userXpMap.set(walletAddress, updated);
    return updated;
  }

  // Transaction History
  async createTransaction(insertTx: InsertTransactionHistory): Promise<TransactionHistory> {
    const id = randomUUID();
    const tx: TransactionHistory = {
      ...insertTx,
      id,
      timestamp: new Date(),
      fromChain: insertTx.fromChain ?? null,
      toChain: insertTx.toChain ?? null,
      amount: insertTx.amount ?? null,
      asset: insertTx.asset ?? null,
      metadata: insertTx.metadata ?? null,
    };
    this.transactionHistoryMap.set(id, tx);
    return tx;
  }

  async getTransactionsByWallet(walletAddress: string, limit = 50): Promise<TransactionHistory[]> {
    return Array.from(this.transactionHistoryMap.values())
      .filter((tx) => tx.walletAddress === walletAddress)
      .sort((a, b) => b.timestamp.getTime() - a.timestamp.getTime())
      .slice(0, limit);
  }

  async getTransactionByHash(txHash: string): Promise<TransactionHistory | undefined> {
    return Array.from(this.transactionHistoryMap.values()).find(
      (tx) => tx.txHash === txHash
    );
  }

  async updateTransactionStatus(
    id: string,
    status: "pending" | "completed" | "failed"
  ): Promise<TransactionHistory | undefined> {
    const tx = this.transactionHistoryMap.get(id);
    if (tx) {
      tx.status = status;
      this.transactionHistoryMap.set(id, tx);
    }
    return tx;
  }

  async getTotalTransactionCount(): Promise<number> {
    return this.transactionHistoryMap.size;
  }

  async getUniqueWalletCount(): Promise<number> {
    const uniqueWallets = new Set<string>();
    const transactions = Array.from(this.transactionHistoryMap.values());
    for (const tx of transactions) {
      uniqueWallets.add(tx.walletAddress);
    }
    return uniqueWallets.size;
  }
}

export const storage = new MemStorage();

client/src/pages/Network.tsx.content:
import { NetworkMap } from "@/components/NetworkMap";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Activity, TrendingUp, Clock } from "lucide-react";
import { useNetwork } from "@/hooks/useNetwork";
import { useXcmActivity } from "@/hooks/useXcmActivity";
import { Alert, AlertDescription } from "@/components/ui/alert";
import { Skeleton } from "@/components/ui/skeleton";

export default function Network() {
  const { data: nodes, isLoading: nodesLoading } = useNetwork();
  const { data: xcmActivity, isLoading: xcmLoading } = useXcmActivity();

  const positionNodes = (nodeList: any[]) => {
    const positions = [
      { x: 400, y: 80 },
      { x: 250, y: 200 },
      { x: 550, y: 200 },
      { x: 150, y: 320 },
      { x: 650, y: 320 },
    ];

    return nodeList.map((node, idx) => ({
      ...node,
      x: positions[idx]?.x || 400,
      y: positions[idx]?.y || 200,
    }));
  };

  return (
    <div className="space-y-6">
      <div>
        <h1 className="text-4xl font-bold mb-2">Network Topology</h1>
        <p className="text-muted-foreground">
          Visualize parachain connections and cross-chain message (XCM) activity
        </p>
      </div>

      {nodesLoading ? (
        <div className="h-96 bg-card rounded-lg animate-pulse" />
      ) : nodes && nodes.length > 0 ? (
        <NetworkMap nodes={positionNodes(nodes)} />
      ) : (
        <Alert>
          <AlertDescription>
            Unable to load network data. Please try again later.
          </AlertDescription>
        </Alert>
      )}

      <Card>
        <CardHeader>
          <CardTitle className="flex items-center gap-2">
            <Activity className="h-5 w-5" />
            Live XCM Activity
          </CardTitle>
        </CardHeader>
        <CardContent>
          {xcmLoading ? (
            <div className="space-y-3">
              {[1, 2, 3].map((i) => (
                <Skeleton key={i} className="h-20 w-full" />
              ))}
            </div>
          ) : xcmActivity && xcmActivity.length > 0 ? (
            <div className="space-y-3">
              {xcmActivity.map((activity, idx) => (
                <div
                  key={idx}
                  className="flex items-center justify-between p-4 bg-muted/30 rounded-lg hover-elevate"
                  data-testid={`xcm-activity-${idx}`}
                >
                  <div className="flex-1">
                    <div className="flex items-center gap-2 mb-2">
                      <span className="font-semibold text-sm">{activity.from}</span>
                      <span className="text-muted-foreground"></span>
                      <span className="font-semibold text-sm">{activity.to}</span>
                    </div>
                    <div className="flex items-center gap-4 text-xs text-muted-foreground">
                      <span className="flex items-center gap-1">
                        <TrendingUp className="h-3 w-3" />
                        {activity.volume24h}
                      </span>
                      <span className="flex items-center gap-1">
                        <Clock className="h-3 w-3" />
                        {activity.lastTransfer}
                      </span>
                    </div>
                  </div>
                  <div className="text-right">
                    <div className="font-mono text-lg font-bold">{activity.count}</div>
                    <div className="text-xs text-muted-foreground">transfers</div>
                    <div className="text-xs text-muted-foreground mt-1">{activity.assets}</div>
                  </div>
                </div>
              ))}
            </div>
          ) : (
            <Alert>
              <AlertDescription>
                No XCM activity data available. Check back soon.
              </AlertDescription>
            </Alert>
          )}
        </CardContent>
      </Card>
    </div>
  );
}

client/src/components/MultiWalletConnect.content:
import { Button } from "@/components/ui/button";
import {
  DropdownMenu,
  DropdownMenuContent,
  DropdownMenuItem,
  DropdownMenuTrigger,
  DropdownMenuSeparator,
  DropdownMenuLabel,
} from "@/components/ui/dropdown-menu";
import { Copy, LogOut, Circle, ChevronDown, User } from "lucide-react";
import { SiPolkadot, SiEthereum } from "react-icons/si";
import { useToast } from "@/hooks/use-toast";
import { useMultiWallet } from "@/hooks/useMultiWallet";

export function MultiWalletConnect() {
  const { toast } = useToast();
  const {
    walletState,
    isConnecting,
    connectPolkadot,
    connectEthereum,
    disconnectPolkadot,
    disconnectEthereum,
    selectPolkadotAccount,
    isPolkadotConnected,
    isEthereumConnected,
    activePolkadotAccount,
  } = useMultiWallet();

  const handleConnectPolkadot = async () => {
    try {
      await connectPolkadot();
      toast({
        title: "Polkadot Wallet Connected",
        description: "Successfully connected to Polkadot.js extension",
      });
    } catch (error) {
      toast({
        title: "Connection Failed",
        description: error instanceof Error ? error.message : "Failed to connect Polkadot wallet",
        variant: "destructive",
      });
    }
  };

  const handleConnectEthereum = async () => {
    try {
      await connectEthereum();
      toast({
        title: "MetaMask Connected",
        description: "Successfully connected to MetaMask",
      });
    } catch (error) {
      toast({
        title: "Connection Failed",
        description: error instanceof Error ? error.message : "Failed to connect MetaMask",
        variant: "destructive",
      });
    }
  };

  const handleDisconnectPolkadot = () => {
    disconnectPolkadot();
    toast({
      title: "Polkadot Disconnected",
      description: "Your Polkadot wallet has been disconnected",
    });
  };

  const handleDisconnectEthereum = () => {
    disconnectEthereum();
    toast({
      title: "MetaMask Disconnected",
      description: "Your Ethereum wallet has been disconnected",
    });
  };

  const copyAddress = (address: string, name: string) => {
    navigator.clipboard.writeText(address);
    toast({
      title: "Address Copied",
      description: `${name} address copied to clipboard`,
    });
  };

  const truncateAddress = (addr: string) => {
    if (!addr) return "";
    return `${addr.slice(0, 6)}...${addr.slice(-6)}`;
  };

  return (
    <div className="flex gap-2">
      {/* Polkadot Wallet */}
      {!isPolkadotConnected ? (
        <Button
          onClick={handleConnectPolkadot}
          data-testid="button-connect-polkadot"
          disabled={isConnecting}
          className="gap-2"
        >
          <SiPolkadot className="h-4 w-4" />
          {isConnecting ? "Connecting..." : "Connect Polkadot"}
        </Button>
      ) : (
        <DropdownMenu>
          <DropdownMenuTrigger asChild>
            <Button variant="outline" data-testid="button-polkadot-menu" className="gap-2">
              <SiPolkadot className="h-4 w-4" />
              <Circle className="h-2 w-2 fill-green-500 text-green-500" />
              <span className="font-mono text-sm">
                {truncateAddress(walletState.polkadot.address || "")}
              </span>
            </Button>
          </DropdownMenuTrigger>
          <DropdownMenuContent align="end" className="w-80">
            <DropdownMenuLabel className="flex items-center gap-2">
              <SiPolkadot className="h-4 w-4" />
              Polkadot Accounts
              <span className="ml-auto text-xs text-muted-foreground">
                {walletState.polkadot.accounts.length} account{walletState.polkadot.accounts.length !== 1 ? 's' : ''}
              </span>
            </DropdownMenuLabel>
            <DropdownMenuSeparator />

            {/* Account Selection */}
            {walletState.polkadot.accounts.map((account) => {
              const isActive = account.address === walletState.polkadot.address;
              return (
                <DropdownMenuItem
                  key={account.address}
                  onClick={() => {
                    if (!isActive) {
                      selectPolkadotAccount(account.address);
                      toast({
                        title: "Account Switched",
                        description: `Now viewing ${account.meta.name || "unnamed account"}`,
                      });
                    }
                  }}
                  data-testid={`button-select-account-${account.address}`}
                  className="flex items-start gap-2 py-3"
                >
                  <div className="flex-1">
                    <div className="flex items-center gap-2">
                      <User className="h-3 w-3" />
                      <span className="font-medium text-sm">
                        {account.meta.name || "Unnamed Account"}
                      </span>
                      {isActive && (
                        <Circle className="h-2 w-2 fill-green-500 text-green-500 ml-auto" />
                      )}
                    </div>
                    <div className="font-mono text-xs text-muted-foreground mt-1">
                      {truncateAddress(account.address)}
                    </div>
                  </div>
                </DropdownMenuItem>
              );
            })}

            <DropdownMenuSeparator />

            <DropdownMenuItem 
              onClick={() => copyAddress(walletState.polkadot.address || "", "Polkadot")} 
              data-testid="button-copy-polkadot-address"
            >
              <Copy className="h-4 w-4 mr-2" />
              Copy Active Address
            </DropdownMenuItem>

            <DropdownMenuItem onClick={handleDisconnectPolkadot} data-testid="button-disconnect-polkadot">
              <LogOut className="h-4 w-4 mr-2" />
              Disconnect All Accounts
            </DropdownMenuItem>
          </DropdownMenuContent>
        </DropdownMenu>
      )}

      {/* Ethereum Wallet */}
      {!isEthereumConnected ? (
        <Button
          onClick={handleConnectEthereum}
          data-testid="button-connect-ethereum"
          disabled={isConnecting}
          variant="outline"
          className="gap-2"
        >
          <SiEthereum className="h-4 w-4" />
          MetaMask
        </Button>
      ) : (
        <DropdownMenu>
          <DropdownMenuTrigger asChild>
            <Button variant="outline" data-testid="button-ethereum-menu" className="gap-2">
              <SiEthereum className="h-4 w-4" />
              <Circle className="h-2 w-2 fill-green-500 text-green-500" />
              <span className="font-mono text-sm">
                {truncateAddress(walletState.ethereum.address || "")}
              </span>
            </Button>
          </DropdownMenuTrigger>
          <DropdownMenuContent align="end" className="w-72">
            <div className="px-2 py-1.5 text-xs text-muted-foreground">
              Connected to Ethereum
            </div>
            <DropdownMenuItem 
              onClick={() => copyAddress(walletState.ethereum.address || "", "Ethereum")} 
              data-testid="button-copy-ethereum-address"
            >
              <Copy className="h-4 w-4 mr-2" />
              <span className="font-mono text-xs truncate">{walletState.ethereum.address}</span>
            </DropdownMenuItem>
            <DropdownMenuItem onClick={handleDisconnectEthereum} data-testid="button-disconnect-ethereum">
              <LogOut className="h-4 w-4 mr-2" />
              Disconnect
            </DropdownMenuItem>
          </DropdownMenuContent>
        </DropdownMenu>
      )}
    </div>
  );
}

