import { useState, useEffect } from "react";
import { ApiPromise, WsProvider } from "@polkadot/api";
import { useWallet } from "@/contexts/WalletContext";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Skeleton } from "@/components/ui/skeleton";
import { Alert, AlertDescription } from "@/components/ui/alert";
import { RefreshCw, Activity, Filter, X } from "lucide-react";
import { Switch } from "@/components/ui/switch";
import { Label } from "@/components/ui/label";

interface Transaction {
  hash: string;
  blockNumber: number;
  method: string;
  section: string;
  signer: string;
  timestamp: Date;
}

export default function Transactions() {
  const { activePolkadotAccount } = useWallet();
  const [transactions, setTransactions] = useState<Transaction[]>([]);
  const [isConnected, setIsConnected] = useState(false);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);
  const [showUserOnly, setShowUserOnly] = useState(false);
  const [api, setApi] = useState<ApiPromise | null>(null);

  useEffect(() => {
    let unsubscribe: (() => void) | null = null;
    let apiInstance: ApiPromise | null = null;

    const connectAndSubscribe = async () => {
      try {
        setIsLoading(true);
        setError(null);

        const provider = new WsProvider("wss://rpc.polkadot.io");
        apiInstance = await ApiPromise.create({ provider });
        await apiInstance.isReady;

        setApi(apiInstance);
        setIsConnected(true);
        setIsLoading(false);

        // Subscribe to new block headers
        unsubscribe = await apiInstance.rpc.chain.subscribeNewHeads(async (header) => {
          try {
            const blockNumber = header.number.toNumber();
            const blockHash = await apiInstance!.rpc.chain.getBlockHash(blockNumber);
            const signedBlock = await apiInstance!.rpc.chain.getBlock(blockHash);

            const newTransactions: Transaction[] = [];

            signedBlock.block.extrinsics.forEach((ex, index) => {
              const signer = ex.signer?.toString() || "System";

              newTransactions.push({
                hash: ex.hash.toString(),
                blockNumber,
                method: ex.method.method,
                section: ex.method.section,
                signer,
                timestamp: new Date(),
              });
            });

            setTransactions((prev) => {
              const combined = [...newTransactions, ...prev];
              // Keep last 100 transactions
              return combined.slice(0, 100);
            });
          } catch (err) {
            console.error("Error processing block:", err);
          }
        });
      } catch (err) {
        console.error("Connection error:", err);
        setError("Failed to connect to Polkadot network. Please try again.");
        setIsLoading(false);
      }
    };

    connectAndSubscribe();

    return () => {
      if (unsubscribe) {
        unsubscribe();
      }
      if (apiInstance) {
        apiInstance.disconnect();
      }
    };
  }, []);

  const handleRefresh = () => {
    // Clear transactions - new ones will continue streaming in from the active WebSocket
    setTransactions([]);
  };

  const filteredTransactions = showUserOnly && activePolkadotAccount
    ? transactions.filter((tx) => tx.signer === activePolkadotAccount.address)
    : transactions;

  const displayedTransactions = filteredTransactions.slice(0, 50);

  return (
    <div className="container mx-auto p-6 max-w-7xl">
      <div className="mb-8 flex items-center justify-between">
        <div>
          <h1 className="text-3xl font-bold mb-2" data-testid="text-page-title">
            Live Transactions
          </h1>
          <p className="text-muted-foreground">
            Real-time Polkadot blockchain transaction feed
          </p>
        </div>
        <div className="flex items-center gap-2">
          <Badge
            variant={isConnected ? "default" : "secondary"}
            data-testid="badge-connection-status"
          >
            <Activity className="h-3 w-3 mr-1" />
            {isConnected ? "Live" : "Offline"}
          </Badge>
          <Button
            variant="outline"
            size="sm"
            onClick={handleRefresh}
            data-testid="button-refresh"
          >
            <RefreshCw className="h-4 w-4" />
          </Button>
        </div>
      </div>

      {error && (
        <Alert variant="destructive" className="mb-6" data-testid="alert-error">
          <AlertDescription>{error}</AlertDescription>
        </Alert>
      )}

      <Card className="mb-6" data-testid="card-filters">
        <CardHeader>
          <div className="flex items-center justify-between">
            <div>
              <CardTitle>Filters</CardTitle>
              <CardDescription>Customize your transaction view</CardDescription>
            </div>
            {showUserOnly && (
              <Button
                variant="ghost"
                size="sm"
                onClick={() => setShowUserOnly(false)}
                data-testid="button-clear-filters"
              >
                <X className="h-4 w-4 mr-1" />
                Clear
              </Button>
            )}
          </div>
        </CardHeader>
        <CardContent>
          <div className="flex items-center gap-2">
            <Switch
              id="user-only"
              checked={showUserOnly}
              onCheckedChange={setShowUserOnly}
              disabled={!activePolkadotAccount}
              data-testid="switch-user-only"
            />
            <Label htmlFor="user-only" className="cursor-pointer">
              Show only my transactions
              {!activePolkadotAccount && (
                <span className="text-muted-foreground ml-2">(connect wallet first)</span>
              )}
            </Label>
          </div>
        </CardContent>
      </Card>

      <Card data-testid="card-transactions">
        <CardHeader>
          <CardTitle>
            Recent Transactions
            <span className="text-muted-foreground font-normal ml-2">
              ({displayedTransactions.length} {showUserOnly ? "matching" : "shown"})
            </span>
          </CardTitle>
          <CardDescription>
            Live feed of the latest 50 transactions on Polkadot
          </CardDescription>
        </CardHeader>
        <CardContent>
          <div className="space-y-3 max-h-[600px] overflow-y-auto pr-2" data-testid="list-transactions">
            {isLoading ? (
              Array.from({ length: 5 }).map((_, i) => (
                <Skeleton key={i} className="h-24 w-full" />
              ))
            ) : displayedTransactions.length === 0 ? (
              <div className="text-center py-12 text-muted-foreground" data-testid="text-no-transactions">
                <Filter className="h-12 w-12 mx-auto mb-4 opacity-50" />
                <p>
                  {showUserOnly
                    ? "No transactions found for your address"
                    : "Waiting for new transactions..."}
                </p>
              </div>
            ) : (
              displayedTransactions.map((tx) => (
                <Card
                  key={tx.hash}
                  className="hover-elevate"
                  data-testid={`transaction-${tx.hash.slice(0, 8)}`}
                >
                  <CardContent className="p-4">
                    <div className="flex items-start justify-between gap-4">
                      <div className="flex-1 space-y-2">
                        <div className="flex items-center gap-2">
                          <Badge variant="outline" data-testid="badge-method">
                            {tx.section}.{tx.method}
                          </Badge>
                          <span className="text-xs text-muted-foreground">
                            Block #{tx.blockNumber}
                          </span>
                        </div>
                        <div className="space-y-1">
                          <div className="flex items-center gap-2">
                            <span className="text-xs text-muted-foreground">Hash:</span>
                            <code className="text-xs font-mono bg-muted px-2 py-0.5 rounded">
                              {tx.hash.slice(0, 16)}...{tx.hash.slice(-8)}
                            </code>
                          </div>
                          <div className="flex items-center gap-2">
                            <span className="text-xs text-muted-foreground">Signer:</span>
                            <code className="text-xs font-mono bg-muted px-2 py-0.5 rounded">
                              {tx.signer === "System" ? "System" : `${tx.signer.slice(0, 8)}...${tx.signer.slice(-6)}`}
                            </code>
                            {activePolkadotAccount && tx.signer === activePolkadotAccount.address && (
                              <Badge variant="default" className="text-xs">You</Badge>
                            )}
                          </div>
                        </div>
                      </div>
                      <div className="text-right text-xs text-muted-foreground">
                        {tx.timestamp.toLocaleTimeString()}
                      </div>
                    </div>
                  </CardContent>
                </Card>
              ))
            )}
          </div>
        </CardContent>
      </Card>
    </div>
  );
}

server/services/staking.ts.content:
import { ApiPromise, WsProvider } from "@polkadot/api";
import type { StakingInfo, StakingAnalytics, ValidatorInfo } from "@shared/schema";

// Singleton API instance per RPC endpoint
const API_INSTANCES = new Map<string, ApiPromise>();

async function getPolkadotApi(rpcUrl: string): Promise<ApiPromise> {
  let api = API_INSTANCES.get(rpcUrl);

  if (api) {
    if (api.isConnected) {
      return api;
    } else {
      // Disconnect old instance
      await api.disconnect();
      API_INSTANCES.delete(rpcUrl);
    }
  }

  // Create new instance
  const provider = new WsProvider(rpcUrl);
  api = await ApiPromise.create({ provider });
  API_INSTANCES.set(rpcUrl, api);

  return api;
}

// Cleanup function for graceful shutdown
export async function disconnectAll() {
  const entries = Array.from(API_INSTANCES.entries());
  for (const [url, api] of entries) {
    try {
      await api.disconnect();
      console.log(`Disconnected from ${url}`);
    } catch (error) {
      console.error(`Error disconnecting from ${url}:`, error);
    }
  }
  API_INSTANCES.clear();
}

export async function getStakingInfo(
  address: string,
  rpcUrl: string = "wss://rpc.polkadot.io"
): Promise<StakingInfo> {
  try {
    const api = await getPolkadotApi(rpcUrl);

    // Fetch account info for free balance
    const accountInfo = await api.query.system.account(address);
    const accountData = accountInfo.toJSON() as any;
    const free = accountData?.data?.free || "0";

    // Query staking ledger for bonded balance
    const stakingLedger = await api.query.staking.ledger(address);
    const ledgerData = stakingLedger.toJSON() as any;

    // Query nominations
    const nominators = await api.query.staking.nominators(address);
    const nominatorData = nominators.toJSON() as any;

    // Query bonded controller account
    const bonded = await api.query.staking.bonded(address);
    const bondedController = bonded.toJSON() as string | null;

    // Calculate balances
    const bondedBalance = ledgerData?.active || "0";
    const unlocking = ledgerData?.unlocking || [];
    const nominations = nominatorData?.targets || [];

    // Calculate total active stake
    const activeStake = bondedBalance;

    // Format unlocking schedule
    const unlockingFormatted = unlocking.map((unlock: any) => ({
      value: unlock.value || "0",
      era: unlock.era || 0,
    }));

    // Query current era for reward calculations
    const currentEra = await api.query.staking.currentEra();
    const currentEraNum = currentEra.toJSON() as number;

    // Query rewards (simplified - just returns pending as 0 for now)
    // Full implementation would query staking.erasRewardPoints and calculate payouts
    const rewardsPending = "0";
    const rewardsTotal = "0";

    const stakingInfo: StakingInfo = {
      walletAddress: address,
      bondedBalance: bondedBalance.toString(),
      freeBalance: free.toString(),
      activeStake: activeStake.toString(),
      nominations: nominations,
      rewardsPending,
      rewardsTotal,
      unlocking: unlockingFormatted,
      status: "online",
      lastUpdated: new Date().toISOString(),
    };

    return stakingInfo;
  } catch (error) {
    console.error("Staking info fetch error:", error);
    return {
      walletAddress: address,
      bondedBalance: "0",
      freeBalance: "0",
      activeStake: "0",
      nominations: [],
      rewardsPending: "0",
      rewardsTotal: "0",
      unlocking: [],
      status: "error",
      lastUpdated: new Date().toISOString(),
    };
  }
}

export async function getStakingRewards(
  address: string,
  eraCount: number = 10,
  rpcUrl: string = "wss://rpc.polkadot.io"
): Promise<{ era: number; reward: string }[]> {
  try {
    const api = await getPolkadotApi(rpcUrl);
    const currentEra = await api.query.staking.currentEra();
    const currentEraNum = currentEra.toJSON() as number;

    const rewards: { era: number; reward: string }[] = [];

    // Query the last N eras for reward points
    for (let i = 0; i < eraCount; i++) {
      const era = currentEraNum - i;
      if (era < 0) break;

      // Query era reward points (this is simplified)
      // Full implementation would check if validator/nominator earned rewards
      const rewardPoints = await api.query.staking.erasRewardPoints(era);
      const rewardData = rewardPoints.toJSON() as any;

      // For now, return 0 - full implementation would calculate actual rewards
      rewards.push({
        era,
        reward: "0",
      });
    }

    return rewards;
  } catch (error) {
    console.error("Staking rewards fetch error:", error);
    return [];
  }
}

