import { useQuery } from '@tanstack/react-query';
import type { GovernanceSummary } from '@shared/schema';

export function useGovernanceSummary(walletAddress?: string) {
  return useQuery<GovernanceSummary>({
    queryKey: ['/api/governance/summary', walletAddress],
    refetchInterval: 60000, // Refetch every minute
    queryFn: async () => {
      const params = walletAddress ? `?walletAddress=${encodeURIComponent(walletAddress)}` : '';
      const response = await fetch(`/api/governance/summary${params}`);

      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }

      const json = await response.json();

      if (json.success && json.data) {
        return json.data;
      }

      throw new Error(json.error || 'Failed to fetch governance summary');
    },
  });
}

sdk/js/utils.ts.content:
export function formatBalance(value: string | number, decimals: number = 10): string {
  const num = typeof value === 'string' ? parseFloat(value) : value;
  return (num / 10 ** decimals).toFixed(4);
}

export function formatAddress(address: string, prefixLength: number = 6, suffixLength: number = 4): string {
  if (address.length <= prefixLength + suffixLength) {
    return address;
  }
  return `${address.slice(0, prefixLength)}...${address.slice(-suffixLength)}`;
}

export function parseAmount(amount: string | number, decimals: number = 10): string {
  const num = typeof amount === 'string' ? parseFloat(amount) : amount;
  return (num * 10 ** decimals).toFixed(0);
}

client/src/hooks/useXcmActivity.ts.content:
import { useQuery } from '@tanstack/react-query';
import { setCache, getCacheWithAge } from '@/lib/cache';
import type { XcmActivity } from '@shared/schema';

export function useXcmActivity() {
  return useQuery<XcmActivity[]>({
    queryKey: ['/api/network/xcm'],
    refetchInterval: 30000, // Refetch every 30 seconds for dynamic updates
    staleTime: 20000,
    queryFn: async () => {
      try {
        const response = await fetch('/api/network/xcm');

        if (!response.ok) {
          throw new Error(`HTTP error! status: ${response.status}`);
        }

        const json = await response.json();

        if (json.success && json.data) {
          setCache('xcm-activity', json.data);
          return json.data;
        }

        throw new Error(json.error || 'Failed to fetch XCM activity');
      } catch (error) {
        console.error('Error fetching XCM activity, trying cache:', error);

        const cached = getCacheWithAge<XcmActivity[]>('xcm-activity');
        if (cached) {
          console.log('Using cached XCM activity data');
          return cached.data;
        }

        throw error;
      }
    },
  });
}

